"use client";

import React, { useState } from 'react'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

const PLACEHOLDER_IMG_SRC =
  'data:image/svg+xml;base64,'

export function ImageWithFallback(props: React.ImgHTMLAttributes<HTMLImageElement>) {
  const [didError, setDidError] = useState(false)

  const handleError = () => {
    setDidError(true)
  }

  const { src, alt, style, className, ...rest } = props

  // Handle empty or invalid src
  const imageSrc = src && src.trim() && src !== 'null' && src !== 'undefined' ? src : PLACEHOLDER_IMG_SRC

  // If src was empty, show placeholder immediately
  if (!src || !src.trim() || src === 'null' || src === 'undefined') {
    return (
      <div
        className={`inline-block bg-gray-100 text-center align-middle ${className ?? ''}`}
        style={style}
      >
        <div className="flex items-center justify-center w-full h-full">
          <img src={PLACEHOLDER_IMG_SRC} alt={alt || "No image"} {...rest} />
        </div>
      </div>
    )
  }

  return didError ? (
    <div
      className={`inline-block bg-gray-100 text-center align-middle ${className ?? ''}`}
      style={style}
    >
      <div className="flex items-center justify-center w-full h-full">
        <img src={ERROR_IMG_SRC} alt="Error loading image" {...rest} data-original-url={src} />
      </div>
    </div>
  ) : (
    <img src={imageSrc} alt={alt} className={className} style={style} {...rest} onError={handleError} />
  )
}
